def blockfont(text, style="standard", max_length=20):

    if style in ASCII_STYLES:
        style_dict = ASCII_STYLES[style]

        # Wrap the text with boundaries 
        lines_of_text = textwrap.wrap(text, width=max_length)

        
        block_lines = []

        for wrapped_text in lines_of_text:
            lines = ["" for _ in range(5)]  

            for char in wrapped_text:
                pattern = style_dict.get(char, ["     "] * 5)
                for i in range(5):
                    lines[i] += pattern[i] + "  "

            block_lines.append("\n".join(lines))

        
        return "\n\n".join(block_lines)

    else:
        try:
            return pyfiglet.figlet_format(text, font=style)
        except pyfiglet.FontNotFound:
            available = list(ASCII_STYLES.keys())
            return f"❌ Font '{style}' not found. Available custom styles: {', '.join(available)} or any pyfiglet font."
