class ResourceType:
    '''
    Specifies resource type.
    '''

    # Image resource.
    Image = "image"

    # Pdf resource.
    Pdf = "pdf"

    # LayoutData resource.
    LayoutData = "layoutData"

    # Dlex resource.
    Dlex = "dlex"

    # Font resource.
    Font = "font"

    # Html resource.
    Html = "html"

    # Word resource.
    Word = "word"
    
    # Excel resource.
    Excel = "excel"