question.template:
from __future__ import annotations

import os
import re
import shutil

import duckdb

MAX_ROWS = 50
MAX_WIDTH = 500

# Use Twig to get students answer
student_answer = """{{ STUDENT_ANSWER | e('py') }}""".rstrip()

# Parsing the student answer
if not student_answer.endswith(";"):
    student_answer = student_answer + ";"
if "pragma" in student_answer.lower():
    raise ValueError("It is not allowed to use PRAGMA statements.")

# Getting the database file
db_files = [fname for fname in os.listdir() if fname.endswith(".db")]
if len(db_files) == 0:
    db_working = ":memory:"
elif len(db_files) == 1:
    db_working = db_files[0]  # Strip .db extension
else:
    raise Exception("Multiple DB files not implemented yet, sorry!")

SEPARATOR = "#<ab@17943918#@>#"

# Running each test in isolation
{% for TEST in TESTCASES %}

if db_working != ":memory:":
    db_working = f"{db_working}.copy"
    shutil.copyfile(db_files[0], db_working)  # Copy clean writeable db file

testcode = """{{ TEST.testcode | e('py') }}"""
extra = """{{ TEST.extra | e('py') }}"""
con = duckdb.connect(db_working, config={"temp_directory": os.getcwd()})

# Set DB parameters
con.sql("SET memory_limit = '{{ QUESTION.memlimitmb - 50 }}MB'; SET threads = 1;")

# Execute student answer
con.sql(student_answer)

# Execute test code
statements = [s for s in testcode.split(";") if s.strip()]
for statement in statements:
    try:
        res = con.sql(statement)
        res.show(max_width=MAX_WIDTH, max_rows=MAX_ROWS) if res else print(res)
    except duckdb.ConstraintException as e:
        # DuckDB prints the individual constraint implementation in the error message
        # so we have to filter it out.
        match = re.search(
            r"^Constraint Error: CHECK constraint failed on table (.+?) .*$", str(e)
        )
        if match:
            print(f"CHECK constraint failed on table {match.group(1)}")
        else:
            print(e)
    except duckdb.Error as e:
        print(e)
    except Exception as e:
        raise (e)

con.close()

{% if not loop.last %}
print(SEPARATOR)
{% endif %}
{% endfor %}

testcode.text:
-- Column names
SELECT name AS name_columns FROM pragma_table_info("thisisatest") ORDER BY name;
---------- -- Column types -- fixed types WITH flex_cols AS (SELECT unnest(map_keys(MAP {})) AS name_type), student_datatype AS (SELECT name AS name_type, TYPE AS datatype FROM pragma_table_info("thisisatest") ORDER BY name) SELECT name_type, datatype FROM student_datatype ANTI JOIN flex_cols ON student_datatype.name_type = flex_cols.name_type ORDER BY name_type;
-- flexible types WITH flextype_table AS (SELECT unnest(map_entries(MAP {}), RECURSIVE := TRUE)), student_datatype AS (SELECT name AS name_type, TYPE AS datatype FROM pragma_table_info("thisisatest") ORDER BY name), flextype_flat AS (SELECT "key" AS col, unnest("value") AS datatype FROM flextype_table), correct_types AS (SELECT flextype_flat.col AS flexible_column_name FROM flextype_flat JOIN student_datatype ON flextype_flat.col = student_datatype.name_type AND flextype_flat.datatype = student_datatype.datatype) SELECT flexible_column_name, '## viable_flex_type ##' AS flex_type FROM correct_types UNION ALL SELECT DISTINCT flextype_flat.col AS flexible_column_name, '## non_viable_flex_type ##' AS flex_type FROM flextype_flat ANTI JOIN correct_types ON flextype_flat.col = correct_types.flexible_column_name;
---------- -- Not null columns
SELECT name AS name_notnull FROM pragma_table_info("thisisatest") WHERE "notnull" ORDER BY name;
---------- -- Unique constraint
SELECT unnest(constraint_column_names) AS name_unique FROM duckdb_constraints() WHERE SCHEMA_NAME='main' AND constraint_type = 'UNIQUE' AND TABLE_NAME='thisisatest' ORDER BY name_unique;
---------- -- Primary key columns
SELECT name AS name_primarykey FROM pragma_table_info("thisisatest") WHERE pk ORDER BY name;
---------- -- Foreign key columns
SELECT unnest(constraint_column_names) AS name_fk_from, constraint_text AS fk_constraint_text FROM duckdb_constraints() WHERE SCHEMA_NAME='main' AND constraint_type = 'FOREIGN KEY' AND TABLE_NAME='thisisatest' ORDER BY name_fk_from;

